body.script:
    // Example XML string
    const xmlString = `


            Learning XML
            John Doe
            Tech Books Publishing
            2022


            Advanced XML Techniques
            Jane Smith
            Code Press
            2023

    `;

    // Parse the XML
    const parser = new DOMParser();
    const xmlDoc = parser.parseFromString(xmlString, "application/xml");

    // Get book elements
    const books = xmlDoc.getElementsByTagName("book");
    const bookList = document.getElementById("book-list");

    // Loop through books and display them
    for (let i = 0; i < books.length; i++) {
        const title = books[i].getElementsByTagName("title")[0].textContent;
        const author = books[i].getElementsByTagName("author")[0].textContent;
        const publisher = books[i].getElementsByTagName("publisher")[0].textContent;
        const year = books[i].getElementsByTagName("year")[0].textContent;

        // Create a book div
        const bookDiv = document.createElement("div");
        bookDiv.className = "book";
        bookDiv.innerHTML = `
${title}
Author: ${author}
Publisher: ${publisher}
Year: ${year}`;

        // Append to book list
        bookList.appendChild(bookDiv);
    }
